// BlogPost.js

import React from 'react';
import { useParams, Link } from 'react-router-dom';

const BlogPost = () => {
  const { id } = useParams();
  // Dummy blog post data
  const post = { id: parseInt(id), title: `Blog Post ${id}`, content: `This is the content of Blog Post ${id}.` };

  return (
    <div>
      <h2>{post.title}</h2>
      <p>{post.content}</p>
      <Link to={`/post/${id}/about`} className="about-link">About the Blog</Link> {/* Add Link to the about page */}
      <br />
      <Link to="/">Go Back</Link>
    </div>
  );
};

export default BlogPost;
