import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import NewBlogPostForm from './NewBlogPostForm';

const HomePage = () => {
  const [blogPosts, setBlogPosts] = useState([
    { id: 1, title: 'First Blog Post', excerpt: 'This is the excerpt of the first blog post.', category: 'Travel' },
    { id: 2, title: 'Second Blog Post', excerpt: 'This is the excerpt of the second blog post.', category: 'Food' },
    { id: 3, title: 'Culture Blog', excerpt: 'Live the variuos cultural practices of hte world' }
  ]);

  const handleAddBlogPost = (newBlogPost) => {
    setBlogPosts([...blogPosts, { id: blogPosts.length + 1, ...newBlogPost }]);
  };

  return (
    <div className="flex flex-col items-center">
      <h2 className="text-2xl font-bold mb-4">Blog Posts</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {blogPosts.map(post => (
          <div key={post.id} className="bg-gray-800 rounded-lg overflow-hidden shadow-md">
            <div className="p-4">
              <h3 className="text-xl font-bold mb-2 text-white">{post.title}</h3>
              <p className="text-gray-200">{post.excerpt}</p>
              <p className="text-gray-200 mt-2">Category: {post.category}</p>
            </div>
            <Link to={`/post/${post.id}`} className="block bg-blue-500 text-white text-center py-2 hover:bg-blue-600">Read More</Link>
          </div>
        ))}
      </div>
      <div className="mt-8">
        <h2 className="text-xl font-bold mb-4">Add New Blog Post</h2>
        <NewBlogPostForm onAdd={handleAddBlogPost} />
      </div>
    </div>
  );
};

export default HomePage;
