// HomePage.js

import React from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const blogPosts = [
    { id: 1, title: 'First Blog Post', excerpt: 'This is the excerpt of the first blog post.', category: 'Travel' },
    { id: 2, title: 'Second Blog Post', excerpt: 'This is the excerpt of the second blog post.', category: 'Food' },
    { id: 3, title: 'Culture Blog', excerpt: 'Live the various cultural practices of the world.' }
  ];

  return (
    <div className="flex flex-col items-center">
      <h2 className="text-2xl font-bold mb-4">Blog Posts</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {blogPosts.map(post => (
          <div key={post.id} className="bg-gray-800 rounded-lg overflow-hidden shadow-md">
            <div className="p-4">
              <h3 className="text-xl font-bold mb-2 text-white">{post.title}</h3>
              <p className="text-gray-200">{post.excerpt}</p>
            </div>
            <Link to={`/post/${post.id}`} className="btn btn-ghost">Read More</Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
