import React from 'react';
import { useParams, Link } from 'react-router-dom';

const BlogPost = () => {
  const { id } = useParams();
  const post = {
    id: parseInt(id),
    title: `Blog Post ${id}`,
    category: id % 3 === 0 ? 'Travel' : id % 3 === 1 ? 'Food' : 'Culture',
    content: `This is the content of Blog Post ${id}.`,
    largeText: id % 3 === 0 ? 'Traveling is fun and adventurous.' : id % 3 === 1 ? 'Food is delicious and brings people together.' : 'Culture encompasses the beliefs, behaviors, and traditions of a society.'
  };

  return (
    <div className="flex flex-col">
      <h2 className="text-2xl font-bold mb-4">{post.title}</h2>
      <p className="text-gray-600 mb-4">{post.content}</p>
      <h3 className="text-xl font-bold mb-2">Category: {post.category}</h3>
      <h3 className="text-xl font-bold mb-2">Large Text:</h3>
      <p className="text-lg text-gray-700 mb-4">{post.largeText}</p>
      <Link to={`/post/${id}/about`} className="btn glass">About the Blog</Link>
      <Link to="/" className="btn btn-secondary">Go Back</Link>
    </div>
  );
};

export default BlogPost;
