import { Category } from '@mui/icons-material';
import React from 'react';
import { useParams } from 'react-router-dom';

const BlogInfoPage = () => {
  const { id } = useParams();

  // Sample data for categories and authors
  const categoryInfo = {
    1: { name: 'Travel', detail: 'Explore the world through travel experiences.' },
    2: { name: 'Food', detail: 'Discover different cuisines and food cultures from around the globe.' },
    3: { name: 'Education', detail: 'Learn about various educational topics and insights.' }
  };

  // Sample data for authors
  const authorInfo = {
    1: 'John Doe',
    2: 'Jane Smith',
    3: 'Alice Johnson'
  };

  // Get category information based on id
  const { name: categoryName, detail: categoryDetail } = categoryInfo[id];

  // Get author information based on id
  const author = authorInfo[id];

  return (
    <div className="flex flex-col">
      <h4 className="text-2xl font-bold mb-2">Blog Information for Post {id}</h4>
      <div className="flex items-center mb-4">
        <Category className="mr-2" /> {/* Material-UI icon for category */}
        <span className="text-lg font-bold">{categoryName}</span>
      </div>
      <p className="mb-4">{categoryDetail}</p>
      <p className="text-gray-600">Author: {author}</p>
    </div>
  );
};

export default BlogInfoPage;
